INSERT INTO `messagestemplates` (`uid`, `projectID`, `eventID`, `title`, `deliveryType`, `msgType`, `subject`, `body`, `images`, `created`) VALUES
(1, 1, 0, 'Guest Invite Email', 'email', 'guestInvite', '{firstName} - You''ve been invited to the Alicia Keys concert in {eventName}', '<!doctype html>\r\n<head>\r\n<meta charset="utf-8">\r\n<title>{subject}</title>\r\n</head>\r\n<body style="background:#000000;color:#ffffff;font-family:Helvetica,Arial,sans-serif;">\r\n<div style="margin:auto;display:block; width:600px; padding: 10px 5px;">\r\n<img src="{img-header}" alt="BlackBerry Presents Alicia Keys" title="BlackBerry Presents Alicia Keys" style="display:block; margin:0; background:#000000;" />\r\n<p style="display:block; width:520px; margin:0; padding: 10px 40px; background:#00a8df; text-transform:uppercase; font-size:18px; color:#ffffff">{eventName}</p>\r\n\r\n<p>You have been invited to join the BlackBerry team as we head out on the road with Alicia Keys on her "Set The World on Fire" Tour. You are just a few clicks away from receiving your ticket to the {eventName} event on {eventDate}.</p>\r\n\r\n<p>We can''t wait for you to join us and experience the BlackBerry Z10!</p>\r\n\r\n<p>Please confirm your acceptance to join us and <a style="color:#00a8df;" href="{regURL}">click here</a> to register.  You may also use this url directly: <a style="color:#00a8df;" href="{regURL}">{regURL}</a></p>\r\n\r\n<p>As a reminder, this invitation is non-transferable and only entitles you to one ticket. Substitutions may be permitted, please contact your BlackBerry Representative for details.</p>\r\n\r\n<p>For complete registration terms and conditions please go to <a style="color:#00a8df;" href="https://BlackBerryPresentsAliciaKeys.com/terms.html">https://BlackBerryPresentsAliciaKeys.com/terms.html</a></p>\r\n\r\n<p>We look forward to seeing you!</p>\r\n</div>\r\n</body>\r\n</html>', '{"img-header":"img/email_invite_header.jpg"}', '2013-02-24 17:08:42'),
(2, 1, 0, 'RSVP - Yes', 'email', 'rsvp-yes', 'Confirmation of attendence to BlackBerry Presents Alicia Keys in {eventName}', '<!doctype html>\r\n<head>\r\n<meta charset="utf-8">\r\n<title>{subject}</title>\r\n</head>\r\n<body style="background:#000000;color:#ffffff;font-family:Helvetica,Arial,sans-serif;">\r\n<div style="margin:auto;display:block; width:600px; padding: 10px 5px;">\r\n<img src="{img-header}" alt="BlackBerry Presents Alicia Keys" title="BlackBerry Presents Alicia Keys" style="display:block; margin:0; background:#000000;" />\r\n<p style="display:block; width:520px; margin:0; padding: 10px 40px; background:#00a8df; text-transform:uppercase; font-size:18px; color:#ffffff">{eventName}</p>\r\n\r\n<p>Thanks for registering, {firstName}.</p>\r\n\r\n<p>You are now an official guest of the BlackBerry&reg; presents Alicia Keys "Set the World on Fire" Tour.</p>\r\n\r\n<p>We are thrilled that you’ll be joining us on {eventDate} in {eventName}.</p>\r\n\r\n<p>Here are your Event Details:<br>\r\n\r\n<p>Venue information:<br>\r\n{venueName}<br>\r\n{venueAddress}<br>\r\n<a href="tel:{venuePhone}" style="color:#00a8df;">{venuePhone}</a><br>\r\n<a style="color:#00a8df;" href="{venueWebsite}">{venueWebsite}</a></p>\r\n\r\n<p>Please come to the BlackBerry VIP Will Call on the evening of the event, after the doors open, to collect your ticket(s). Please print a copy of this email and present on arrival, along with proof of ID, required for each guest.</p>\r\n\r\n<p>As a reminder, this invitation is non-transferable. Substitutions may be permitted, please contact your BlackBerry Representative for details.</p>\r\n\r\n<p>For complete registration terms and conditions please go to <a style="color:#00a8df;" href="https://BlackBerryPresentsAliciaKeys.com/terms.html">https://BlackBerryPresentsAliciaKeys.com/terms.html</a></p>\r\n\r\n<p>Please contact your business unit manager with any questions or concerns.</p>\r\n\r\n<p>We look forward to seeing you!</p>\r\n</div>\r\n</body>\r\n</html>', '{"img-header":"img/email_invite_header.jpg"}', '2013-02-24 19:38:43'),
(3, 1, 0, 'RSVP - No', 'email', 'rsvp-no', 'Sorry you can''t make it to BlackBerry Presents Alicia Keys in {eventName}.', '<html>\n<head>\n<title>{subject}</title>\n</head>\n<body style="background:#000000;color:#ffffff;font-family:Helvetica,Arial,sans-serif;">\n<div style="width:520px;margin:auto;">\n<img src="{inviteEmailHeader}" alt="BlackBerry" title="BlackBerry Presents Alicia Keys" style="display:block; margin:0; background:#000000;" />\n<p style="display:block; width:50px; margin:0; padding: 10px 40px; background:#00a8df; text-transform:uppercase; font-size:18px;">{eventName}</p>\n<p style="display:block; width:600px; margin:0; padding: 10px 5px; background:#000000; color:#ffffff; font-family:Helvetica,Arial,sans-serif; padding-top:35px;">\nThanks for registering {firstName},\n</p>\n<p style="display:block; width:600px; margin:0; padding: 10px 5px;">\nWe''re sorry you won''t be joining us at BlackBerry Presents Alicia Keys in {eventName}.\n</p>\n<p style="display:block; width:600px; margin:0; padding: 10px 5px;">\nFor complete registration terms and conditions please go to <a style="color:#00a8df; text-decoration:none;" href="https://BlackBerryPresentsAliciaKeys.com/terms">https://BlackBerryPresentsAliciaKeys.com/terms</a>\n</p>\n<p style="display:block; width:600px; margin:0; padding: 10px 5px;">\nAs a reminder, this invitation is non-transferable and is only valid for one ticket.  Substitutions may be permitted, please contact your BlackBerry Representative for details.\n</p>\n<p style="display:block; width:600px; margin:0; padding: 10px 5px; padding-bottom: 100px">\nFor complete registration terms and conditions please go to <a style="color:#00a8df; text-decoration:none;" target="_blank" href="https://BlackBerryPresentsAliciaKeys.com">https://BlackBerryPresentsAliciaKeys.com</a><br>\nor contact <a style="color:#00a8df; text-decoration:none;" href="mailto:eventmanager@BlackBerryPresentsAliciaKeys.com">eventmanager@BlackBerryPresentsAliciaKeys.com</a>\n</p>\n</div>\n</body>\n</html>', '{"img-header":"img/email_invite_header.jpg"}', '2013-02-24 19:38:43'),
(4, 1, 0, 'User Reg Email', 'email', 'userReg', 'BlackBerry and Alicia Keys are prepped and ready to Set the World on Fire.  Now all we need is you!', '<!doctype html>\n<head>\n<meta charset="utf-8">\n<title>{subject}</title>\n</head>\n<body style="background:#000000;color:#ffffff;font-family:Helvetica,Arial,sans-serif;">\n<div style="margin:auto;display:block; width:600px; padding: 10px 5px;">\n<img src="{img-header}" alt="BlackBerry Presents Alicia Keys" title="BlackBerry Presents Alicia Keys" style="display:block; margin:0; background:#000000;" />\n<p style="display:block; width:520px; margin:0; padding: 10px 40px; background:#00a8df; text-transform:uppercase; font-size:18px; color:#ffffff">{eventName}</p>\n<p>You are just a few clicks away from being part of the most anticipated concert experience of the year. We have teamed up with our partner, Global Creative Director and Grammy award-winning artist, Alicia Keys, and are set to hit the road for a years worth of concerts spanning 5 continents.</p>\n<p>Now it''s your turn to gather your team. Just click below and give us the names of your partners, associates, friends and fans. That''s it. We''ll do the rest.</p>\n<p>Confirm your account here: <a href="{regURL}" style="color:#00a8df">{regURL}</a></p>\n<p>Log in, upload your VIP list and we will contact them and arrange their tickets!  All you have to do is show up and be ready to host at the BlackBerry Presents Alicia Keys "Set The World On Fire" Tour</p>\nInvite Process:\n<ul>\n<li>To confirm your requested allocation number please contact your BlackBerry representative.</li>\n<li>Use the official guest list template to enter all guest details, which your representative can provide.</li>\n<li>We cannot guarantee adjacent seating, however if you would like to group guests we will endeavor to seat all parties together.</li>\n<li>To arrange parties into groups and request adjacent seating... please assign a group name (where indicated) to each individual within the group (one unique name per group.)</li>\n<li>A single person authorized to pick up the entire group''s tickets may be assigned by indicating the person''s name under GROUP WILL CALL NAME, but is not required. </li>\n<li>Once your guest list is complete with all data please upload at <a href="{siteURL}" style="color:#00a8df">{siteURL}</a>.</li>\n<li>Upon upload your guests will be sent a confirmation email inviting them to register for their ticket.</li>\n<li>Once they have accepted the terms and conditions they will be sent a unique ticket number and details of their local event.</li>\n<li>Guest changes can be made online and individual guests added individually where required – up to your unit''s allocation.</li>\n<li>We will send communications prior to event confirming Will Call box office location.</li>\n<li>We can offer tailored messages to your guests. Just contact<a href="mailto:reba@ncompassoline.com" style="color:#00a8df">reba@ncompassoline.com</a> for details.</li>\n<li>To book hospitality please visit <a href="{siteURL}" style="color:#00a8df">{siteURL}</a>.</li>\n<li>Please contact <a href="mailto:reba@ncompassoline.com" style="color:#00a8df">reba@ncompassonline.com</a> with any ticketing inquiries.</li>\n<li>Please don''t forget to add your name to the list to receive your ticket!</li>\n</ul>\n<p>\nSee you in {eventName}!\n</p>\n</div>\n</body>\n</html>', '{"img-header":"img/email_invite_header.jpg"}', '2013-02-26 10:19:56'),
(5, 1, 0, 'Reg Error', 'email', 'regError', 'There was an error processing a user''s registration.', '{email} ({firstName} {lastName}) attempted to register, resulting in an unknown error.<br><br>\r\nBlackBerry Events', '{"img-header":"img/email_invite_header.jpg"}', '2013-02-26 11:25:45'),
(6, 1, 0, 'Password Recovery Email', 'email', 'passReset', '{firstName} - Here is your password recovery email.', 'Hello {firstName} {lastName},<br>\r\n<br>\r\nYou recently requested a password reset. Your new password is {newPass}. You can now log in with this new password.<br>\r\n<br>\r\nThank you,<br>\r\nBlackBerry Events', '{"img-header":"img/email_invite_header.jpg"}', '2013-02-27 19:07:10'),
(7, 1, 0, 'Hospitality Request', 'email', 'hospitalityRequest', 'Hospitality Request from {bu} for {eventName} on {eventDate}.', '<!doctype html>\n<head>\n<meta charset="utf-8">\n<title>{subject}</title>\n</head>\n<body style="background:#000000;color:#ffffff;font-family:Helvetica,Arial,sans-serif;">\n\n<h2>Hospitality Request</h2>\n\n<table cellpadding="3" cellspacing="2">\n<tr><td>Quantity:<td></td> {quantity}</td></tr>\n<tr><td>Type:<td></td> {type}</td></tr>\n<tr><td>Special Requests:<td></td>{special_requests}</td></tr>\n<tr><td>Business Unit:<td></td>{bu}</td></tr>\n<tr><td>Person Requesting:<td></td>{person_requesting}</td></tr>\n<tr><td>Invoice To:<td></td>{invoice_to}</td></tr>\n<tr><td>Billing Address:<td></td>{address}</td></tr>\n<tr><td>City:<td></td>{city}</td></tr>\n<tr><td>State/Province:<td></td>{state}</td></tr>\n<tr><td>Postal Code:<td></td>{postal}</td></tr>\n<tr><td>Country:<td></td>{country}</td></tr>\n<tr><td>Payment Method:<td></td>{payment_method}</td></tr>\n<tr><td>PO Number:<td></td>{po_number}</td></tr>\n</table>\n</div>\n</body>\n</html>', '', '2013-03-01 12:15:15'),
(8, 1, 0, 'Mass Guest Template', 'email', 'massTemplate', 'Message to all guests attending BlackBerry Presents Alicia Keys in {eventName}', '<!doctype html>\n<head>\n<meta charset="utf-8">\n<title>{subject}</title>\n</head>\n<body style="background:#000000;color:#ffffff;font-family:Helvetica,Arial,sans-serif;">\n<div style="margin:auto;display:block; width:600px; padding: 10px 5px;">\n<img src="{img-header}" alt="BlackBerry Presents Alicia Keys" title="BlackBerry Presents Alicia Keys" style="display:block; margin:0; background:#000000;" />\n<p style="display:block; width:520px; margin:0; padding: 10px 40px; background:#00a8df; text-transform:uppercase; font-size:18px; color:#ffffff">{eventName}</p>\n<p>\n{msgBody}\n</p>\n</div>\n</body>\n</html>', '{"img-header":"img/email_invite_header.jpg"}', '2013-03-07 17:18:27');

